import HTTPStatus from 'http-status';

/**
 * @api Application Abstract Controller class.
 *
 * @export
 * @class Abstract
 */
export default class Abstract {
  /**
   * Abstract constructor.
   *
   * @constructor
   * @memberof Abstract
   * @param {object} model - DI model required for CRUD methods.
   */
  constructor(model) {
    // Detect whether this constructor was called using the new operator.
    // new.target returns a reference to the constructor or function.
    if (new.target === Abstract) {
      throw new TypeError('Cannot construct Abstract instances directly.');
    }

    // Cannot operate without a model so lets throw an error immediately if one is not provided.
    if (model === undefined || this.isEmpty(model)) {
      throw new TypeError('A model object is required.');
    }

    this._model = model;
    this.getByName = this.getByName.bind(this);
    this.getList = this.getList.bind(this);
    this.create = this.create.bind(this);
  }

  /**
   * Test if an object is empty.
   *
   * @memberof Abstract
   * @param {object} obj to be tested.
   * @return {boolean}
   */
  isEmpty(obj) {
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) return false;
    }
    return true;
  }

  /**
   * @api {get} /endpoint/:name (http://example.com/api/icons/person-icon)
   * @apiDescription Get single item by name.
   * @apiName getByName
   * @apiGroup Abstract
   * @apiParam {string} name
   * @apiErrorExample {json} Error
   *
   * HTTP/1.1 404 Not Found
   *
   * @apiSuccessExample Success-Response:
   *
   * HTTP/1.1 200 OK
   *
   *  {
   *    "tags": ["solid", "wire"],
   *    "versions": [],
   *    "name": "user-icon",
   *    "version": "0",
   *  }
   *
   * @memberof Abstract
   * @param {string} req
   * @param {string} res
   * @param {any} next
   * @return {json}
   */
  async getByName(req, res) {
    try {
      const data = await this._model.findOne({name: req.params.name});
      return res.status(HTTPStatus.OK).json(data);
    } catch (error) {
      return res.status(HTTPStatus.BAD_REQUEST).json(error);
    }
  }

  /**
   * @api {get} /endpoint Get list of items (http://example.com/api/icons)
   * @apiDescription Get a list of items
   * @apiName getList
   * @apiGroup Abstract
   * @apiErrorExample {json} Error
   *
   * HTTP/1.1 404 Not Found
   *
   * @apiSuccessExample Success-Response:
   *
   * HTTP/1.1 200 OK
   *
   * [
   *  {
   *    "tags": ["solid", "wire"],
   *    "versions": [],
   *    "name": "user-icon",
   *    "version": "0",
   *  },
   *  {
   *    "tags": ["solid", "wire"],
   *    "versions": [],
   *    "name": "lock-icon",
   *    "version": "0",
   *  }
   * ]
   *
   * @memberof Abstract
   * @param {string} req
   * @param {string} res
   * @param {any} next
   * @return {json}
   */
  async getList(req, res) {
    try {
      const data = await this._model.find();
      return res.status(HTTPStatus.OK).json(data);
    } catch (error) {
      return res.status(HTTPStatus.BAD_REQUEST).json(error);
    }
  }

  /**
   * @api {post} /endpoint Create item (http://example.com/api/icons)
   * @apiDescription Create item requires body obj.
   * @apiName create
   * @apiGroup Abstract
   *
   * @apiErrorExample {json} Error
   *
   * HTTP/1.1 404 Not Found
   *
   * @apiSuccessExample Success-Response:
   * HTTP/1.1 200 OK
   *
   *
   * @apiParam (Body) {Object} name Icon name.
   *
   * @apiParamExample {json} Post-Example:
   *
   *  {
   *    "tags": ["solid", "wire"],
   *    "versions": [],
   *    "name": "user-icon",
   *    "version": "0",
   *  }
   *
   * @memberof Abstract
   * @param {string} req
   * @param {string} res
   * @param {any} next
   * @return {json}
   */
  async create(req, res) {
    try {
      const body = req.body;
      const data = await this._model.create(body);
      return res.status(HTTPStatus.CREATED).json(data);
    } catch (error) {
      return res.status(HTTPStatus.BAD_REQUEST).json(error);
    }
  }
}

